import React, { use, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getDatabase, ref, push } from 'firebase/database';


export function UploadProfile(props) {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [proImage, setProImage] = useState(null);
    const [intro, setIntro] = useState('');
    const [roles, setRoles] = useState([]);
    const [submitStatus, setSubmitStatus] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!name || !email || !intro || roles.length === 0) {
            setErrorMessage("Please fill out required fields *");
            setSubmitStatus(null);
            window.scrollTo({ top: 0, behavior: "smooth" });
            return;
        }

        try {
            // const formData = {
            //     name,
            //     email,
            //     phone,
            //     intro,
            //     roles

            const res = await fetch('/profile/', {
                method: 'GET',              
                credentials: 'include',     //
            });
            const { username } = await res.json(); 

            // test adding this again
            // not adding?
            await fetch("profile/posts", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                   // username: username ?? '',
                    name: name,
                    email: email,
                    phone: phone,
                    picture: proImage,
                    role: roles,
                    description: intro
                })
            })
            //const db = getDatabase();
            // const studentRef = ref(db, `Students`);
            // await push(studentRef, formData);
            console.log("Data submitted successfully");

            event.target.reset();

            setName('');
            setEmail('');
            setPhone('');
            setProImage(null);
            setIntro('');
            setSubmitStatus("success");
            setErrorMessage("");

        } catch (error) {
            console.error("Error submitting data: ", error);
            setSubmitStatus("error");
            setErrorMessage("Error submitting data, please try again.");
        }
        window.scrollTo({ top: 0, behavior: "smooth" });
    };


    const handleNameChange = (event) => {
        setName(event.target.value);
    }
    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    }
    const handlePhoneChange = (event) => {
        setPhone(event.target.value);
    }
    const handleImageChange = (event) => {
        setProImage(event.target.value);
    }
    const handleDescriptionChange = (event) => {
        setIntro(event.target.value);
    }
    const handleRoleChange = (event) => {
        const { value, checked } = event.target;
        if (checked) {
            if (roles.length >= 2) return;
            setRoles([...roles, value]);
        } else {
            setRoles(roles.filter((role) => role !== value));
        }
    }

    return (
        <>
            {/* Hello world */}
            <div className="form-container">
                <form onSubmit={handleSubmit}>
                    {/* Existing form fields */}
                    {submitStatus === "success" && (
                        <div className="alert_success">
                            Data submitted successfully!
                        </div>
                    )}

                    {(submitStatus === "error" || errorMessage) && (
                        <div className="alert_message">
                            {errorMessage}
                        </div>
                    )}

                    <label htmlFor="name">Name <span style={{ color: "red" }}>*</span></label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        placeholder="Enter your name"
                        onChange={handleNameChange}
                        value={name}
                        required=""
                    />
                    <label htmlFor="email">Email <span style={{ color: "red" }}>*</span></label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        placeholder="Enter your email"
                        onChange={handleEmailChange}
                        value={email}
                        required=""
                    />
                    <label htmlFor="phone">Phone</label>
                    <input
                        type="tel"
                        id="phone"
                        name="phone"
                        placeholder="Enter your phone number"
                        onChange={handlePhoneChange}
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        value={phone}
                        required=""
                    />
                    <small>Format: 123-456-7890</small>
                    {/* upload profile picture */}
                    <div className="upload-profile-picture">
                        <legend>
                            <strong>Upload Profile Picture:</strong>
                        </legend>
                        <input
                            type="file"
                            id="profile-picture"
                            name="profile-picture"
                            accept="image/*"
                            onChange={handleImageChange}
                        />
                    </div>
                    {/* Role Selection */}
                    <div className="roles">
                        <legend>Role (Select up to 2) <span style={{ color: "red" }}>*</span></legend>
                        <label>
                            <input type="checkbox" name="role" defaultValue="Product Manager" onChange={handleRoleChange} disabled={roles.length >= 2 && !roles.includes("Product Manager")} />
                            <span>Product Manager</span>
                        </label>
                        <label>
                            <input type="checkbox" name="role" defaultValue="Software Engineer" onChange={handleRoleChange} disabled={roles.length >= 2 && !roles.includes("Software Engineer")} />
                            <span>Software Engineer</span>
                        </label>
                        <label>
                            <input type="checkbox" name="role" defaultValue="Data Science" onChange={handleRoleChange} disabled={roles.length >= 2 && !roles.includes("Data Science")} />
                            <span>Data Science</span>
                        </label>
                        <label>
                            <input type="checkbox" name="role" defaultValue="Designer" onChange={handleRoleChange} disabled={roles.length >= 2 && !roles.includes("Designer")} />
                            <span>Designer</span>
                        </label>
                        <label>
                            <input type="checkbox" name="role" defaultValue="Researcher" onChange={handleRoleChange} disabled={roles.length >= 2 && !roles.includes("Researcher")} />
                            <span>Researcher</span>
                        </label>
                    </div>
                    <div className="self-introduction">
                        <legend>
                            <strong>Self Introduction <span style={{ color: "red" }}>*</span></strong>
                        </legend>
                        <textarea
                            id="self-introduction"
                            name="self-introduction"
                            rows={5}
                            placeholder="Write about yourself..."
                            value={intro}
                            onChange={handleDescriptionChange}
                        />
                    </div>
                    {/* Submit Button */}
                    <button type="submit">Submit</button>
                </form>
            </div>
        </>
    );
}
